import 'package:flutter/foundation.dart';class SnowfallController {  /// Internal callback filled by the widget  VoidCallback? stopCallback;  VoidCallback? pauseCallback;  VoidCallback? resumeCallback;  VoidCallback? startCallback;  void stop() => stopCallback?.call(); // call this to stop snowfall naturally  void pause() => pauseCallback?.call(); // call this to freeze snow mid air  void resume() => resumeCallback?.call(); // call this to resume the snow fall  void start() => startCallback?.call(); // call this to start from scratch}